import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import locro from '../../assets/locro.jpg';
import goulash from '../../assets/goulash.jpeg';

const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Locro Criollo",
    description: "Locro (from the Quechua ruqru) is a hearty thick squash stew. ",
    ingredients: "Ingredients: Squash, corn, beans, meat, red chorizo.",
    price: 22.99,
    image: locro
  },
  {
    id: "m2",
    name: "Cazuela de Carne",
    description: "A german specialty!",
    ingredients: "Ingredients: Squash, corn, beans, meat, red chorizo.",
    price: 16.5,
    image: goulash
  },
  {
    id: "m3",
    name: "Cazuela de Pollo",
    description: "American, raw, meaty",
    ingredients: "Ingredients: Squash, corn, beans, meat, red chorizo.",
    price: 12.99,
  },
  {
    id: "m4",
    name: "Mondongo",
    description: "Healthy...and green...",
    ingredients: "Ingredients: Squash, corn, beans, meat, red chorizo.",
    price: 18.99,
  },
];

const AvailableMeals = () => {
  const mealsList = DUMMY_MEALS.map((meal) => (
    <MealItem
      id={meal.id} // this is new!
      key={meal.id}
      name={meal.name}
      description={meal.description}
      ingredients={meal.ingredients}
      price={meal.price}
      image={meal.image}
    />
  ));

  return (
 

    <section className={classes.meals}>
   
        <Card><ul>{mealsList}</ul></Card>
   
    </section>

  );
};

export default AvailableMeals;
